class Price extends PluginPrice
{
  public function getFullName()
  {
    sfApplicationConfiguration::getActive()->loadHelpers(array('Number'));
    return $this->description.' ('.$this->name.'), '.format_currency($this->value,'â‚¬');
  }
  
  public function getWorkspaceIds()
  {
    $arr = array();
    foreach ( $this->Workspaces as $ws )
      $arr[] = $ws->id;
    return $arr;
  }
  
  public function isAccessibleBy(sfSecurityUser $user, $option = NULL)
  {
    return in_array($user->getId(), $this->Users->getPrimaryKeys());
  }
}
