class PriceManifestation extends PluginPriceManifestation
{
  public static function createPrice(Price $price)
  {
    $pm = new PriceManifestation();
    $pm->price_id = $price->id;
    $pm->value = $price->value;
    return $pm;
  }
  
  public function preSave($event)
  {
    if ( is_null($this->value) )
      $this->value = $this->Price->value;
    parent::preSave($event);
  }
  
  public function getFullName()
  {
    sfApplicationConfiguration::getActive()->loadHelpers(array('Number'));
    return $this->Price->name.' ('.$this->Price->description.'), '.format_currency($this->value,'â‚¬');
  }
}
